public class operateurassignement {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

	double total = 0;
	double num = 1;
	double pourcentage = 0.50;
	
	
	total = total + num;
	System.out.println(total);
	total = total += num;
	System.out.println(total);	
	total -= num ;
	System.out.println(total);	
	total *= pourcentage;
	System.out.println(total);	
	total /= 2; 
	
	System.out.println(num);
	
	}

}
